SELECT t3.*, CONCAT((@i:=@i+1 ),'') id, CONCAT( round(
IF ( t3.interview / t3.tjl IS NULL, 0, t3.interview / t3.tjl ), 2 )*100, '%' ) msl, CONCAT( round(
IF ( t3.offer / t3.interview IS NULL, 0, t3.offer / t3.interview ), 2 )*100, '%' ) offerl, CONCAT( round(
IF ( t3.rzfq / t3.offer IS NULL, 0, t3.rzfq / t3.offer ), 2 )*100, '%' ) pdl FROM (SELECT @i:=0) as i, ( SELECT t2.optday opdate, t2.opuser, ( SELECT s.cnname FROM sys_user s WHERE s.username = t2.opuser ) opusername, count(DISTINCT(t2.cno)) khs, count(DISTINCT(t2.jobdesc)) jobnum, sum(IF(t2.flowstate = 2, 1, 0)) tjl, sum(IF(t2.flowstate = 4, 1, 0)) interview, sum(IF(t2.flowstate = 5, 1, 0)) offer, sum(IF(t2.flowstate = 6, 1, 0)) rzdg, sum(IF(t2.flowstate = 8 and t2.offq = 1, 1, 0)) rzfq, sum(IF(t2.flowstate = 11, 1, 0)) byqlz FROM ( SELECT t1.*, DATE_FORMAT(t1.optime, '%Y-%m-%d') optday, DATE_FORMAT(t1.optime, '%Y-%m') optmonth, DATE_FORMAT(t1.optime, '%Y') optyear, tj.jobid, tj.jobtitle, j2.jobdesc, j2.cno, j2.cname, (select count(1) from jobs_candidates_state jcs where jcs.jcid = t1.jcid and flowstate=5) offq FROM ( select * from jobs_candidates_state and opuser = #{currentuser} ) t1 LEFT JOIN jobs_candidates tj ON t1.jcid = tj.id LEFT JOIN customers_jobs j2 ON tj.jobid = j2.id
and t1.optime >= #{createtimest}
and t1.optime < DATE_SUB(#{createtimeed},INTERVAL -2 DAY)
and j2.cname like #{qcustomername}
and tj.jobtitle like #{qjobname}
and t1.opusername like #{qopusername}
) t2 WHERE 1 = 1 GROUP BY t2.optday, t2.opuser ORDER BY t2.optday DESC ) t3

