SELECT
    t1.*, DATE_FORMAT(t1.optime, '%Y-%m-%d') optday,
    DATE_FORMAT(t1.optime, '%Y-%m') optmonth,
    DATE_FORMAT(t1.optime, '%Y') optyear,
    tj.jobid,
    j2.jobdesc jobtitle,
    tj.candidateid,
    tj.candidatename,
    j2.cno,
    j2.cname
FROM
    jobs_candidates_state t1
LEFT JOIN jobs_candidates tj ON t1.jcid = tj.id
LEFT JOIN customers_jobs j2 ON tj.jobid = j2.id
WHERE
1=1

    AND (
    DATE_FORMAT(t1.optime, '%Y-%m') = #{opdate}
    OR DATE_FORMAT(t1.optime, '%Y-%m-%d') = #{opdate}
    OR DATE_FORMAT(t1.optime, '%Y') = #{opdate}
    )


AND t1.opuser = #{opuser}


AND cno = #{cno}


AND jobid = #{jobid}


AND flowstate = #{flowstate}


    AND jcid = #{jcid}


    and t1.optime >= #{createtimest}


    and t1.optime < DATE_SUB(#{createtimeed},INTERVAL -2 DAY)

SELECT t4.id, t4.opdate, t4.opuser, t4.opusername, t4.khs, t4.tjl, t4.interview, t4.offer, t4.money, t4.tjf, t4.msf, if(t4.qyf is null,0,t4.qyf) qyf, t4.off, (t4.tjf + t4.msf+if(t4.qyf is null,0,t4.qyf) + t4.off) total FROM ( SELECT CONCAT((@i :=@i + 1), '') id, t3.*, IF (t3.tjl >= 30, 30, t3.tjl) tjf,
IF ( t3.interview * 2 >= 30, 30, t3.interview * 2 ) msf, if(offer>0 or money > 0,(select q.qys from ( select `t`.`createuser` AS `createuser`,`t`.`createtime` AS `createtime`,count(`t`.`cname`) AS `qys` from (select `customers`.`createuser` AS `createuser`,date_format(`customers`.`updatetime`,'%Y-%m') AS `createtime`,`customers`.`cname` AS `cname` from `customers` where (`customers`.`businessstate` = '签约')) `t` group by `t`.`createuser`,`t`.`createtime` ) q where q.createtime=t3.opdate and q.createuser=t3.opuser),0)*10 qyf, if(t3.money is null,0, round(t3.money / 50000, 2)) off FROM (SELECT @i := 0) AS i, ( SELECT t2.optmonth opdate, t2.opuser, ( SELECT s.cnname FROM sys_user s WHERE s.username = t2.opuser ) opusername, count(DISTINCT(t2.cno)) khs, sum(IF(t2.flowstate = 2, 1, 0)) tjl, sum(IF(t2.flowstate = 4, 1, 0)) interview, sum(IF(t2.flowstate = 5, 1, 0)) offer, ( SELECT sum(money) FROM (SELECT `cs1`.`id` AS `id`, `cs1`.`optime` AS `optime`, `cs1`.`text4` AS `text4`, `cs1`.`t1` AS `t1`, date_format(`cs1`.`optime`, '%Y-%m') AS `opmonth`, substring_index(`cs1`.`t1`, ':', 1) AS `opuser`, substring_index(`cs1`.`t1`, ':' ,-(1)) AS `money` FROM ( SELECT `cs`.`id` AS `id`, `cs`.`optime` AS `optime`, `cs`.`text4` AS `text4`, substring_index(`cs`.`text4`, ';', 1) AS `t1` FROM `jobs_candidates_state` `cs` WHERE ( (`cs`.`flowstate` = 5) AND (`cs`.`text4` IS NOT NULL) ) UNION SELECT `cs`.`id` AS `id`, `cs`.`optime` AS `optime`, `cs`.`text4` AS `text4`, substring_index( substring_index(`cs`.`text4`, ';', 2), ';' ,- (1) ) AS `t1` FROM `jobs_candidates_state` `cs` WHERE ( (`cs`.`flowstate` = 5) AND (`cs`.`text4` IS NOT NULL) ) UNION SELECT `cs`.`id` AS `id`, `cs`.`optime` AS `optime`, `cs`.`text4` AS `text4`, substring_index(`cs`.`text4`, ';' ,-(1)) AS `t1` FROM `jobs_candidates_state` `cs` WHERE ( (`cs`.`flowstate` = 5) AND (`cs`.`text4` IS NOT NULL) ) ) `cs1`) v1 WHERE v1.opuser = t2.opuser AND v1.opmonth = t2.optmonth ) money FROM ( SELECT t1.*, DATE_FORMAT(t1.optime, '%Y-%m') optmonth, tj.jobid, tj.jobtitle, j2.jobdesc, j2.cno, j2.cname FROM ( select * from jobs_candidates_state and opuser = #{currentuser} ) t1 LEFT JOIN jobs_candidates tj ON t1.jcid = tj.id LEFT JOIN customers_jobs j2 ON tj.jobid = j2.id where 1=1
and DATE_FORMAT(t1.optime, '%Y-%m') = #{opdate}
and t1.opusername like #{opusername}
) t2 WHERE 1 = 1 GROUP BY t2.optmonth, t2.opuser ORDER BY t2.optmonth DESC ) t3 ) t4
